package com.codebury.simfocus.fragments

import android.content.Context
import android.content.Intent
import android.content.SharedPreferences
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.codebury.simfocus.R
import com.codebury.simfocus.authentication.AuthLoginScreen
import com.codebury.simfocus.helper.ProfileAdapter

class ProfileFragment : Fragment() {

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {

        activity?.actionBar?.hide()

        val view = inflater.inflate(R.layout.fragment_profile, container, false)

        val logOutBtn: Button = view.findViewById(R.id.logout)

        val adapter = ProfileAdapter(
            listOf(
                "https://images.unsplash.com/photo-1495615080073-6b89c9839ce0?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8c3F1YXJlfGVufDB8fDB8fA%3D%3D&w=1000&q=80",
                "https://i0.wp.com/digital-photography-school.com/wp-content/uploads/2011/11/square-format-01.jpg?resize=600%2C600&ssl=1",
                "https://www.dpreview.com/files/p/articles/6566520415/Diallo_110510edit.jpg",
                "https://images.pexels.com/photos/1005644/pexels-photo-1005644.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
                "data:image/jpeg;base64,",
                "https://i.pinimg.com/originals/20/5e/fa/205efac2bb2a9923c3d167bba481b102.jpg",
                "https://images.unsplash.com/photo-1495615080073-6b89c9839ce0?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8c3F1YXJlfGVufDB8fDB8fA%3D%3D&w=1000&q=80",
                "https://i0.wp.com/digital-photography-school.com/wp-content/uploads/2011/11/square-format-01.jpg?resize=600%2C600&ssl=1",
                "https://www.dpreview.com/files/p/articles/6566520415/Diallo_110510edit.jpg",
                "https://images.pexels.com/photos/1005644/pexels-photo-1005644.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
                "data:image/jpeg;base64,",
                "https://i.pinimg.com/originals/20/5e/fa/205efac2bb2a9923c3d167bba481b102.jpg"
            ), requireContext())

        val recyclerView = view.findViewById<RecyclerView>(R.id.profileRecycler)
        recyclerView.isNestedScrollingEnabled = false
        recyclerView.setHasFixedSize(false);
        recyclerView.adapter = adapter


        logOutBtn.setOnClickListener {
            val sharedPreferences: SharedPreferences = context?.getSharedPreferences(
                "kotlinsharedpreference",
                Context.MODE_PRIVATE
            )!!

            val editor: SharedPreferences.Editor = sharedPreferences.edit()

            editor.clear()
            editor.apply()

            activity?.startActivity(Intent(context,AuthLoginScreen::class.java))
            activity?.finish()
        }

        return view
    }

}